mapper.select:
  	 select 

	 from voucher where 
	 status = #{status,jdbcType=INTEGER}

       and merchantId = #{merchantId,jdbcType=VARCHAR} 


	   and start_date  >=  #{start,jdbcType=TIMESTAMP} 


       and end_date  <=  #{end,jdbcType=TIMESTAMP}

	 order by createtime desc

mapper.update:
update voucher
set 
  status = #{status,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}
