mapper.insert:
	    insert into voucher_picture 
		(
			 id, voucher_id, type, indx, name_old, name_new, path
      	)
	    values

	      (
		     #{item.id,jdbcType=VARCHAR}, #{item.voucherId,jdbcType=INTEGER}, #{item.type,jdbcType=VARCHAR}, #{item.indx,jdbcType=INTEGER}, 
      		#{item.nameOld,jdbcType=VARCHAR}, #{item.nameNew,jdbcType=VARCHAR}, #{item.path,jdbcType=VARCHAR}
	      )

mapper.select:
	    select 

	    from voucher_picture
	    where voucher_id = #{voucherId,jdbcType=VARCHAR} 

        	and type = #{type,jdbcType=VARCHAR}
