mapper.select:
  	 select 

	 from event where 
	 status = #{status,jdbcType=INTEGER}

       and merchantId = #{merchantId,jdbcType=VARCHAR} 


	   and startDate  >=  #{start,jdbcType=TIMESTAMP} 


       and endDate  <=  #{end,jdbcType=TIMESTAMP}

	 order by createtime desc

mapper.update:
update event
set 
  status = #{status,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}
